using System.Collections.Generic;using Microsoft.EntityFrameworkCore;using DbContext = Microsoft.EntityFrameworkCore.DbContext;namespace LibraryOfBooksServer.Models{    public class AppDatabaseContext : DbContext    {        public DbSet<Book> Books { get; set; }        public DbSet<Genre> Genres { get; set; }        public AppDatabaseContext()        {        }        public AppDatabaseContext(DbContextOptions<AppDatabaseContext> options) : base(options)        {                        if (Database.EnsureCreated())            {                FirstStartApp();            }        }        protected override void OnModelCreating(ModelBuilder modelBuilder)        {            modelBuilder.Entity<Book>()                .HasOne(book => book.Genre)                .WithMany(genre => genre.Books)                .HasForeignKey(book => book.GenreId)                .OnDelete(DeleteBehavior.Cascade);        }        private void FirstStartApp()        {            var genre1 = new Genre {Name = "Genre 1"};            var genre2 = new Genre {Name = "Genre 2"};            var genre3 = new Genre {Name = "Genre 3"};            var genre4 = new Genre {Name = "Genre 4"};            var genre5 = new Genre {Name = "Genre 5"};            var genre6 = new Genre {Name = "Genre 6"};            var genre7 = new Genre {Name = "Genre 7"};            var genre8 = new Genre {Name = "Genre 8"};            var genre9 = new Genre {Name = "Genre 9"};            var genre10 = new Genre {Name = "Genre 10"};            Genres.Add(genre1);            Genres.Add(genre2);            Genres.Add(genre3);            Genres.Add(genre4);            Genres.Add(genre5);            Genres.Add(genre6);            Genres.Add(genre7);            Genres.Add(genre8);            Genres.Add(genre9);            Genres.Add(genre10);            SaveChanges();            var book1 = new Book {Name = "book1", GenreId = 1};            var book2 = new Book {Name = "book2", GenreId = 1};            var book3 = new Book {Name = "book3"};            var book4 = new Book {Name = "book4"};            var book5 = new Book {Name = "book5", Genre = genre6};            var book6 = new Book {Name = "book6", Genre = genre6};            var book7 = new Book {Name = "book7", Genre = genre5};            var book8 = new Book {Name = "book8", Genre = genre1};            var book9 = new Book {Name = "book9", Genre = genre1};            var book10 = new Book {Name = "book10", Genre = genre1};            var book11 = new Book {Name = "book11", Genre = genre3};            var book12 = new Book {Name = "book12", Genre = genre3};            var book13 = new Book {Name = "book13", Genre = genre2};            var book14 = new Book {Name = "book14", Genre = genre8};            var book15 = new Book {Name = "book15", Genre = genre10};            var book16 = new Book {Name = "book16", Genre = genre9};            var book17 = new Book {Name = "book17", Genre = genre10};            var book18 = new Book {Name = "book18", Genre = genre6};            var book19 = new Book {Name = "book19"};            var book20 = new Book {Name = "book20", Genre = genre8};            var book21 = new Book {Name = "book21"};            var book22 = new Book {Name = "book22", Genre = genre4};            var book23 = new Book {Name = "book23", Genre = genre6};            var book24 = new Book {Name = "book24", Genre = genre7};            var book25 = new Book {Name = "book25", Genre = genre8};            Books.AddRange(new List<Book>            {                book1,                book2,                book3,                book4,                book5,                book6,                book7,                book8,                book9,                book10,                book11,                book12,                book13,                book14,                book15,                book16,                book17,                book18,                book19,                book20,                book21,                book22,                book23,                book24,                book25            });            SaveChanges();        }    }}