using System.Collections.Generic;using Microsoft.EntityFrameworkCore;using DbContext = Microsoft.EntityFrameworkCore.DbContext;namespace LibraryOfBooksServer.Models{    public class AppDatabaseContext : DbContext    {        public DbSet<Book> Books { get; set; }        public DbSet<Genre> Genres { get; set; }        public AppDatabaseContext()        {        }        public AppDatabaseContext(DbContextOptions<AppDatabaseContext> options) : base(options)        {        }        protected override void OnModelCreating(ModelBuilder modelBuilder)        {            modelBuilder.Entity<Book>()                .HasOne(book => book.Genre)                .WithMany(genre => genre.Books)                .HasForeignKey(book => book.GenreId)                .OnDelete(DeleteBehavior.Cascade);        }    }}